import { put } from 'redux-saga/effects'
import ACTION from '../actions/actionTypes'
import { getShopListById, getShopListBySessionId,
    createShopList, updateShopListBySessionId, updateShopListById } from '../../api/rest/shopListService'

export function* getUsersSaga() {
    yield put({type: ACTION.USER_REQUEST});
    try {
        const { data } = yield getAllUsers();
        yield put({ type: ACTION.USERS_RESPONSE, users: data})
    } catch (e) {
        yield put({ type: ACTION.USER_ERROR, error: e})
    }
}
