import ACTION from '../actions/actionTypes';

const initialState = {
    shopListItems: [],
    budget: 0,
    spent: 0,
};

export default function (state = initialState, action) {
    switch (action.type) {
        case ACTION.GET_SHOPLIST_SESSION_ID_RESPONSE: {
            return {
                ...state,
                users: action.users,
                isFetching: false,
                error: null
            }
        }
        case ACTION.USER_BY_ID_RESPONSE: {
            return {
                ...state,
                displayedUser: action.user,
                isFetching: false,
                error: null
            }
        }
        case ACTION.USER_ERROR: {
            return {
                ...state,
                error: action.error,
                isFetching: false
            }
        }
        default: { return state }
    }
}
