import React, { Component } from 'react';
import './Home.sass';
import { Grid, Paper, Input, Button } from '@material-ui/core';
import ItemPanel from '../../components/ItemPanel/ItemPanel'

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 1,
                    name: 'Potato',
                    amount: 10,
                    cost: 10
                },
                {
                    id: 2,
                    name: 'Potato',
                    amount: 10,
                    cost: 10
                },
            ]
        }
    }

    renderItems() {
        console.log(this.state);
        return (
            <>
                {
                    this.state.items.map((i, index) => {
                        return <ItemPanel key={i.id} item={i} />
                    })
                }
            </>
        );
    }

    render() {
        return (
            <div className={'Home-container'}>
                <Grid container spacing={24} alignItems={"center"} direction={"column"} wrap={"nowrap"}>
                    <Grid item container xs={12} sm={12} md={6} lg={6}>
                        <Paper className={'headPanel'}>
                            <Grid container wrap="nowrap" spacing={16}>
                                <Grid item xs={6}>
                                    <Input placeholder="Name" className={'nameInput'} required/>
                                </Grid>
                                <Grid item xs>
                                    <Input placeholder="Amount" className={'secondaryInput'}/>
                                </Grid>
                                <Grid item xs>
                                    <Input placeholder="Cost" className={'secondaryInput'}/>
                                </Grid>
                                <Grid item>
                                    <Button variant="contained" color="primary">
                                        Add
                                    </Button>
                                </Grid>
                            </Grid>
                        </Paper>
                    </Grid>
                    <Grid item container className={'itemsContainer'} direction={"column"}
                          alignItems={"center"} xs={12} sm={12} md={6} lg={6} spacing={16}>
                        { this.renderItems() }
                    </Grid>

                </Grid>
            </div>
        );
    }
}
export default Home;
