import React, { Component } from 'react';
import MenuIcon from '@material-ui/icons/Menu';
import { AppBar, Toolbar, Typography, Button, IconButton, Grid, Input } from '@material-ui/core';
import './Header.sass';
import connect from 'react-redux/es/connect/connect'
import { updateBudget } from "../../store/actions/actionCreator";
const debounce = require('lodash.debounce');
class Header extends Component {

    handleBudgetChange = (newBudget) => {
        console.log(newBudget);
    };
    handleBudgetChangeDebounce = (debounce(this.handleBudgetChange, 1000));

    render() {
        return (
            <div className={'Header-container'}>
                <AppBar position="static">
                    <Toolbar>
                        <Grid container justify={"space-between"} alignItems={"center"}>
                            <IconButton color="inherit" aria-label="Menu">
                                <MenuIcon />
                            </IconButton>
                            <div className={'centerBlock'}>
                                <Typography variant="body1" color="inherit" className={'headerName'}>
                                    Budget:
                                </Typography>
                                <Input defaultValue={ this.props.budget ? this.props.budget : 0 }
                                       onChange={(e) => {this.handleBudgetChangeDebounce(e.target.value)}}
                                       onClick={this.setSelectionRange(0, this.value.length)}
                                />
                                <Typography variant="body1" color="inherit" className={'headerName'}>
                                    Spent: { this.props.shopList.generalCost ? this.props.shopList.generalCost : 0}
                                </Typography>
                            </div>
                            <Button color="inherit">Login</Button>
                        </Grid>
                    </Toolbar>
                </AppBar>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        shopList: state.shoppingReducer.shopList,
        budget: state.shoppingReducer.budget,
    }
};

const mapDispatchToProps = (dispatch) => ({
    updateBudget: (newBudget) => dispatch(updateBudget(newBudget)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Header);
