import React, { Component } from 'react';
import './ItemPanel.sass';
import { Grid, Paper, Input } from '@material-ui/core';
import { Done, Clear }  from '@material-ui/icons'

class ItemPanel extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item: props.item,
            index: props.index
        }
    }
    render() {
        return (
            <Grid item container alignItems={"center"} className={'ItemPanel-container'}>
                <Grid item xs={10} lg={10}>
                    <Paper className={'itemPanel ' + props.className}>
                        <Grid container wrap="nowrap" spacing={16}>
                            <Grid item xs={6}>
                                <Input placeholder="Name" className={'nameInput'} defaultValue={this.state.item.name}/>
                            </Grid>
                            <Grid item xs>
                                <Input placeholder="Amount" className={'secondaryInput'} defaultValue={this.state.item.amount}/>
                            </Grid>
                            <Grid item xs>
                                <Input placeholder="Cost" className={'secondaryInput'} defaultValue={this.state.item.cost}/>
                            </Grid>
                        </Grid>
                    </Paper>
                </Grid>
                <Grid item container xs={2} lg={2} justify={"center"}>
                    <Grid item>
                        <Done className={'panelIcon'} id={'acceptIcon'} fontSize={"large"}
                              onClick={() => {props.onCompleteHandler(this.state.index)}}/>
                    </Grid>
                    <Grid item>
                        <Clear className={'panelIcon'} id={'deleteIcon'} fontSize={"large"}
                               onClick={() => {props.onDeleteHandler(this.state.index)}}/>
                    </Grid>
                </Grid>
            </Grid>
        );
    }
};
export default ItemPanel;
