import React, { Component } from 'react';
import './Home.sass';
import { Grid, Paper, Input, Button } from '@material-ui/core';
import ItemPanel from '../../components/ItemPanel/ItemPanel'
import {getCookie, randomStringValue, setCookie} from "../../utils/cookieUtil";
import {SHOPPING_SESSION} from "../../constants";
import { getShopListBySessionId, createShopList, updateShopListById, updateShopListBySessionId, getShopListById } from "../../api/rest/shopListService";

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            headName: '',
            headCost: 0,
            headAmount: 0,
            items: [
                {
                    name: 'Potato',
                    amount: 10,
                    cost: 10
                },
                {
                    name: 'Potato',
                    amount: 10,
                    cost: 10
                },
            ]
        }
    }
    componentDidMount() {
        if(getCookie(SHOPPING_SESSION)) {
            const shopSession = getCookie(SHOPPING_SESSION);
            getShopListBySessionId(shopSession)
                .then(result => {
                    if(result){
                        console.log("shopList from server:");
                        console.log(result);
                    }
                })
                .catch(err => {
                    console.log("error from server:");
                    console.log(err);
                })
        }
        else {
            let date = new Date;
            date.setDate(date.getDate() + 1);
            const options = {
                path: '/',
                expires: date
            };
            const cookieValue = randomStringValue();
            setCookie(SHOPPING_SESSION, cookieValue, options);
        }
    }

    renderItems() {
        return (
            <>{
                this.state.items.map((i, index) => {
                    return <ItemPanel key={index} item={i}/>
                })
            }</>
        );
    }

    handleInputChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    addNewListItemHandler() {
        const listItem = {
            name: this.state.headName,
            amount: this.state.headAmount,
            cost: this.state.headCost
        };
        this.setState({
            items: this.state.items.concat(listItem)
        });
        console.log(this.state);
    }

    render() {
        return (
            <div className={'Home-container'}>
                <Grid container spacing={24} alignItems={"center"} direction={"column"}>
                    <Grid item container xs={12} sm={8} md={6} lg={5}>
                        <Paper className={'headPanel'}>
                            <Grid container wrap="nowrap" spacing={16}>
                                <Grid item xs={6}>
                                    <Input placeholder="Name" className={'nameInput'} name={'headName'}
                                           required onChange={(e) => {this.handleInputChange(e)}}/>
                                </Grid>
                                <Grid item xs>
                                    <Input placeholder="Amount" className={'secondaryInput'} name={'headAmount'}
                                           onChange={(e) => {this.handleInputChange(e)}}/>
                                </Grid>
                                <Grid item xs>
                                    <Input placeholder="Cost" className={'secondaryInput'} name={'headCost'}
                                           onChange={(e) => {this.handleInputChange(e)}}/>
                                </Grid>
                                <Grid item>
                                    <Button variant="contained" color="primary"
                                            onClick={(e) => {this.addNewListItemHandler(e)}}>
                                        Add
                                    </Button>
                                </Grid>
                            </Grid>
                        </Paper>
                    </Grid>
                    <Grid item container className={'itemsContainer'} direction={"column"}
                          alignItems={"center"} xs={12} sm={8} md={6} lg={5} spacing={8}>
                        { this.renderItems() }
                    </Grid>

                </Grid>
            </div>
        );
    }

}
export default Home;
